<?php	require "TSTagClass.php";	$line = 1;	$generic = "Generic/";	$specific = "Specific/";		$license = file_get_contents("LICENSE.txt");	if (!file_exists($generic)){		mkdir($generic, 0777, true);	}		if (!file_exists($specific)){		mkdir($specific, 0777, true);	}	if ( ( $gestor = fopen("tags.csv", "r") ) ) {		while ( ( $fila = fgetcsv($gestor, 1000, ";") ) ) {					if ($line > 1){				$TSTagclass = new TSTagClass($fila[0], $fila[1], $fila[2], $fila[3], $fila[4], $fila[5], $fila[6], "", "");							$folder = $TSTagclass->getHTMLElementClass() == "HTMLElement" ? $generic : $specific;								$file=fopen($folder.$TSTagclass->getClassNameFirstUpper().".ts","w+") or die("Error");								fputs($file, $TSTagclass->generateClass($license));								fclose($file); 			  }			  			  $line++;		}		echo "Se han creado ".($line-1)." clases";	}?>