<?php	require "TSTagClass.php";	$linea = 1;	$generic = "Generic/";	$specific = "Specific/";	if (!file_exists($generic)){		mkdir($generic, 0777, true);	}		if (!file_exists($specific)){		mkdir($specific, 0777, true);	}	if (($gestor = fopen("tags.csv", "r")) !== FALSE) {		while (($fila = fgetcsv($gestor, 1000, ";")) !== FALSE) {			if ($linea > 1){							$TSTagclass = new TSTagClass($fila[0], $fila[1], $fila[2], $fila[3], $fila[4], $fila[5], $fila[6], "", "");							if ($TSTagclass->getHTMLElementClass() == "HTMLElement"){					$ar=fopen($generic.$TSTagclass->getClassNameFirstUpper().".ts","w+") or die("Problemas en la creacion");				}else{					$ar=fopen($specific.$TSTagclass->getClassNameFirstUpper().".ts","w+") or die("Problemas en la creacion");				}				fputs($ar, $TSTagclass->generateClass());				fclose($ar); 			  }			  			  $linea++;		}		echo "Se han creado ".($linea-1)." clases";	}?>